insert into cropDisease (name, disease, symptom, cure) values ('আলু', 'আলুর কাটুই পোকা', CONCAT_WS(CHAR(10 using utf8), 'লক্ষণ :', ' ', '১। কাটুই পোকার কীড়া বেশ শক্তিশালী, ৪০-৫০ মিলিমিটার লম্বা হয়৷', '২। পোকার উপর পিঠ কালচে বাদামি বর্ণের, পার্শ্বদেশ কালো রেখাযুক্ত এবং বর্ণ ধূসর সবুজ৷ শরীর নরম ও তৈলাক্ত৷', '৩। কাটুই পোকার কীড়া চারা গাছ কেটে দেয় এবং আলুতে ছিদ্র করে আলু ফসলের ক্ষতি করে থাকে৷','৪। পোকার কীড়া দিনের বেলা মাটির নিচে লুকিয়ে থাকে৷ আলুর কাটা গাছ অনেক সময় কাটা গোঁড়ার পাশেই পড়ে থাকতে দেখা যায়৷'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। কাটুই পোকার উপদ্রব খুব বেশি না হলে কাটা আলু গাছ দেখে তার কাছাকাছি মাটি উল্টেপাল্টে কীড়া খুঁজে সংগ্রহ করে মেরে ফেলা উচিৎ৷', '২। কাটুই পোকার উপদ্রব খুব বেশি হলে কীটনাশক ব্যবহার করতে হবে৷ প্রতি লিটার পানির সাথে ক্লোরোপাইরফর (ডারসবান) ২০ ইসি ৫ মিলিমিটার হারে মিশিয়ে গাছের গোড়া ও মাটি স্প্রে করে ভিজিয়ে দিতে হবে৷ আলু লাগানোর ৩০-৪০ দিন পর স্প্রে করতে হবে৷'));


insert into cropDisease (name, disease, symptom, cure) values ('আলু', 'আলুর সুতলী পোকা', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ', ' ',  '১। আলুর সুতলী পোকার মথ আকারে ছোট, ঝালযুক্ত, সরু ডানা বিশিষ্ট ধূসর বাদামি হয়৷', '২। পূর্ণাঙ্গ কীড়া সাদাটে বা হাল্কা গোলাপী বর্ণের এবং ১৫-২০ মিলিমিটার লম্বা হয়ে থাকে৷', '৩। কীড়া আলুর মধ্যে লম্বা সুড়ঙ্গ করে আলুর ক্ষতি করে থাকে৷','৪। বাংলাদেশে বসতবাড়িতে সংরক্ষিত আলু এ পোকার দ্বারা ক্ষতিগ্রস্ত হয়৷'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। বাড়িতে সংরক্ষিত আলু শুকনা বালি, ছাই, তুষ অথবা কাঠের গুড়ার একটি পাতলা স্তর (আলুর উপরে ০.৫ সেন্টিমিটার) দিয়ে ঢেকে দিতে হবে৷', '২। আলু সংরক্ষণ করার আগে সুতলী পোকা আক্রান্ত আলু বেছে ফেলে দিতে হবে৷'));


insert into cropDisease (name, disease, symptom, cure) values ('আলু', 'আলুর মড়ক বা নাবী ধ্বসা (লেইট ব্লাইট) রোগ', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ: ', ' ',  '১। প্রথমে পাতা, ডগা ও কাণ্ডে ছোট ভিজা দাগ পড়ে৷', '২। ক্রমে দাগ বড় হয় ও সমগ্র পাতা, ডগা ও কাণ্ডের কিছু অংশ ঘিরে ফেলে৷','৩। বাতাসের আপেক্ষিক আদ্রতা বেশি থাকলে ২-৩ দিনের মধ্যেই জমির অধিকাংশ ফসল আক্রান্ত হয়ে পড়ে৷','৪। ভোরের দিকে  আক্রান্ত পাতার নিচে সাদা পাউডারের মতো ছত্রাক চোখে পড়ে৷','৫। আক্রান্ত ক্ষেতে পোড়াপোড়া গন্ধ পাওয়া যায় এবং মনে হয় যেন জমির ফসল পুড়ে গেছে৷'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। রোগমুক্ত বীজ ব্যবহার করা৷', '২। আক্রান্ত জমিতে সেচ যথাসম্ভব বন্ধ করে দিতে হবে৷','৩। রোগ দেখা দেওয়ার সাথে সাথে রিডোমিল (০.২%), ডাইথেন এম-৪৫ (০.২%) ইত্যাদি   ছত্রাকনাশক অনুমোদিত হারে ১০-১২ দিন পর পর স্প্রে করতে হবে৷'));


insert into cropDisease (name, disease, symptom, cure) values ('আলু', 'আলুর আগাম ধসা বা আর্লি ব্লাইট রোগ', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ: ', ' ',  '১। নিচের পাতা ছোট ছোট বাদামি রংয়ের অল্প বসে-যাওয়া কৌণিক দাগ পড়ে৷', '২। আক্রান্ত অংশে সামান্য বাদামি এলাকার সাথে পর্যায়ক্রমে কালচে রংয়ের চক্রাকার দাগ অপেক্ষাকৃত লম্বা ধরনের হয়৷','৩। গাছ মরে যাওয়া এ রোগের লক্ষণীয় উপসর্গ৷','৪। আক্রান্ত টিউবারের গায়ে গাঢ় বাদামি থেকে কালচে বসে যাওয়া দাগ পড়ে৷'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। সুষম সার প্রয়োগ এবং সময়মতো সেচ প্রয়োগ নিশ্চিত করতে হবে৷', '২। রোগ দেখা দেওয়ার সাথে সাথে প্রতি লিটার পানিতে ২ গ্রাম রোভরাল মিশিয়ে ৭-১০ দিন পর পর প্রয়োগ করতে হবে৷ ডাইথেন এম-৪৫ ০.২% হারে প্রয়োগ করা যায়৷','৩। আগাম জাতের আলু চাষ করতে হবে৷'));


insert into cropDisease (name, disease, symptom, cure) values ('আলু', 'আলুর স্টেম ক্যাষ্কার স্কার্ফ রোগ', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ: ', ' ',  '১। গজানো অঙ্কুরের মাথায় এবং স্টোলনে আক্রমণের দাগ দেখা যায়৷', '২। বড় গাছের গোঁড়ার দিকে লম্বা লালচে বর্ণের দাগ বা ক্ষতের সৃষ্টি হয়৷','৩। কাণ্ডের সাথে ছোট ছোট টিউবার দেখা যায়৷','৪। আক্রান্ত টিউবারের গায়ে শক্ত কালচে এবং সুপ্ত রোগ জীবাণু গুটি দেখা যায়৷'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। টেরক্লোর হেক্টরপ্রতি ১৫ কেজি মাত্রায় বীজ লাগানোর পূর্বে বীজ নালায় প্রয়োগ করতে হবে৷', '২। ৩% বরিক এসিড় দ্বারা বীজ শোধন বা স্প্রে যন্ত্রের সাহায্যে প্রয়োগ করলেও ভালো ফল পাওয়া যায়৷','৩। বীজ আলু মাটির বেশি গভীরে রোপণ পরিহার করতে হবে৷','৪। ভালোভাবে অঙ্কুরিত বীজ আলু রোপণ করতে হবে৷'));


insert into cropDisease (name, disease, symptom, cure) values ('আলু', 'কাণ্ড ও আলু পচা রোগ', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ: ', ' ',  '১। এ রোগের আক্রমণের ফলে বাদামি দাগ কাণ্ডের গোড়া ছেয়ে ফেলে৷', '২। গাছ ঢলে পড়ে এবং পাতা বিশেষ করে নিচের পাতা হলদে হয়ে যায়৷','৩। আক্রান্ত অংশে বা আশেপাশের মাটিতে ছত্রাকের সাদা সাদা জালিকা দেখা যায়৷','৪। কিছু দিন পর সরিষার দানার মত রোগ জীবাণু গুটি বা স্কেলেরোসিয়া সৃষ্টি হয়৷','৫। আলুর গা থেকে পানি বের হয় এবং পচন ধরে৷ ক্রমে আলু পচে নষ্ট হয়ে যায়৷'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। আক্রান্ত গাছ কিছুটা মাটিসহ সরিয়ে ফেলতে হবে৷', '২। জমি গভীরভাবে চাষ করতে হবে৷','৩। জমিতে সব সময় পঁচা জৈবসার প্রয়োগ করতে হবে৷'));


insert into cropDisease (name, disease, symptom, cure) values ('আলু', 'ঢলে পড়া এবং বাদামি পচন রোগ', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ: ', ' ',  '১। গাছের একটি শাখা বা এক অংশ ঢলে পড়তে পারে৷', '২। পাতা সাধারণত হলুদ হয় না এবং সবুজ অবস্থায়ই চুপসে ঢলে পড়ে৷','৩। গোঁড়ার দিকে গাছের কাণ্ড ফেড়ে দেখলে বাদামি আক্রান্ত এলাকা দেখা যায়৷','৪। ঢলে পড়া গাছ খুব দ্রুত চুপসে যায়৷','৫। আক্রান্ত আলু কাটলে ভিতরে বাদামি দাগ দেখা যায়৷','৬। আলুর চোখে সাদা পুঁজের মতো দেখা যায় এবং আলু অল্প দিনের মধ্যেই পচে যায়৷'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। সুস্থ রোগমুক্ত বীজ ব্যবহার করতে হবে৷', '২। আলু লাগানোর সময় প্রতি হেক্টরে ৮০-ঌ০ কেজি হারে স্ট্যাবল ব্লিচিং পাউডার জমিতে প্রয়োগ করতে হবে৷','৩। পরিমিত মাত্রায় সেচ প্রয়োগ করতে হবে এবং রোগ দেখা দিলে পানি সেচ বন্ধ করে দিতে হবে৷'));


insert into cropDisease (name, disease, symptom, cure) values ('আলু', 'আলুর দাদ রোগ', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ: ', ' ',  '১। হালকা দাদ হলে টিউবারের উপরে উঁচু এবং ভাসা বিভিন্ন আকারের বাদামি দাগ পড়ে৷', '২। রোগের গভীর দাদে গোলাকার গর্ত বা ডাবা দাগ পড়ে৷','৩। রোগের আক্রমণ সাধারণত ত্বকেই সীমাবদ্ধ থাকে৷'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। রোগের আক্রমণ সাধারণত ত্বকেই সীমাবদ্ধ থাকে৷', '২। জমিতে বেশি মাত্রায় নাইট্রোজেন সার ব্যবহার বর্জন করতে হবে৷','৩। ৩% বরিক এসিড দিয়ে বীজ শোধন করে নিতে হবে৷','৪। জমিতে হেক্টরপ্রতি ১২০ কেজি জিপসাম সার প্রয়োগ করতে হবে৷'));


insert into cropDisease (name, disease, symptom, cure) values ('আলু', 'আলুর পাতা মোড়ানো রোগ', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ: ', ' ',  '১। আক্রান্ত গাছের পাতা খসখসে, খাড়া ও উপরের দিকে মুড়ে যায়৷৷', '২। আগার পাতার রং হালকা সবুজ হয়ে যায় এবং গাছের বৃদ্ধি বন্ধ হয়ে যায়৷','৩। আগার পাতার রং হালকা সবুজ হয়ে যায় এবং গাছের বৃদ্ধি বন্ধ হয়ে যায়৷','৪। গাছ খাটো হয় এবং সোজা হয়ে উপরের দিকে দাঁড়িয়ে থাকে৷ আলুর সংখ্যা কমে যায়৷'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। রোগমুক্ত বীজ ব্যবহার করা৷', '২। কীটনাশক (এজেড্রিন, নোভাক্রন, মেনোড্রিন ইত্যাদি) ২ মিলিমিটার অথবা ১ মিলিমিটার ডাইমেক্রন প্রতি লিটার পানিতে মিশিয়ে ৭-১০ দিন পর পর জমিতে স্প্রে করতে হবে৷','৩। আক্রান্ত গাছ টিউবারসহ তুলে ফেলতে হবে৷'));


insert into cropDisease (name, disease, symptom, cure) values ('আলু', 'আলুর মোজাইক রোগ', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ: ', ' ',  '১। পাতায় বিভিন্ন ধরনের ছিটে দাগ পড়ে, পাতা বিকৃত ও ছোট হয়৷', '২। ভাইরাস এবং আলুর জাতের উপর নির্ভর করে লক্ষণ ভিন্নতর হয়৷','৩। ভাইরাস এবং আলুর জাতের উপর নির্ভর করে লক্ষণ ভিন্নতর হয়৷'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। সুস্থ গাছ থেকে বীজ সংগ্রহ করতে হবে এবং রোগমুক্ত বীজ ব্যবহার করতে হবে৷', '২। টমেটো, তামাক এবং কতিপয় সোলানেসি গোত্রভুক্ত আগাছা এ ভাইরাসের বিকল্প পোষক৷  সুতরাং আশেপাশে এ ধরনের গাছ রাখা যাবে না৷ আক্রান্ত গাছ আলুসহ রগিং করে ফেলতে হবে৷','৩। জাব পোকা দমন পদ্ধতি অনুসরণ করতে হবে অর্থাৎ ১ মিলিমিটার ডাইমেক্রন প্রতি লিটার পানিতে মিশিয়ে ৭-১০ দিন পর পর জমিতে স্প্রে করতে হবে৷'));


insert into cropDisease (name, disease, symptom, cure) values ('আলু', 'আলুর হলদে রোগ', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ: ', ' ',  '১। স্থানীয় জাতের আলুতে এ রোগ বেশি হয়৷', '২। পাতা কুঁচকে যায় ও ছিটা দাগ দেখা যায়৷','৩। দূর থেকে আক্রান্ত গাছ সহজেই চোখে পড়ে৷ আলু ছোট হয় বা একেবারেই হয় না৷'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। রোগমুক্ত বীজ ব্যবহার করতে হবে৷', '২। রগিং করে আক্রান্ত গাছ উঠিয়ে ফেলতে হবে৷','৩। কীটনাশক প্রয়োগ করে পাতা ফড়িং দমন করতে হবে৷ এজন্য ডাইমেক্রন (০.১%) ৭-১০ দিন পর স্প্রে করতে হবে৷','৪। অপেক্ষাকৃত বড় আকারের আলু বীজ হিসেবে ব্যবহার করতে হবে৷'));


insert into cropDisease (name, disease, symptom, cure) values ('আলু', 'আলুর শুকনো পচা রোগ', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ: ', ' ',  '১। আলুর গায়ে কিছুটা গভীর কালো দাগ পড়ে৷', '২। আলুর ভিতরে গর্ত হয়ে যায়৷','৩। প্রথম পচন যদিও ভিজা থাকে পরে তা শুকিয়ে শক্ত হয়ে যায়৷','৪। আক্রান্ত অংশে গোলাকার ভাঁজ এবং কখনো কখনো ঘোলাটে সাদা ছত্রাক জালিকা দেখা যায়৷'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। আলু ভালোভাবে বাছাই করে সংরক্ষণ করতে হবে৷', '২। যথাযথ কিউরিং করে আলু গুদামজাত করতে হবে৷','৩। ডাইথেন এম-৪৫ দ্রবণ ০.২% দ্বারা বীজ আলু শোধন করতে হবে৷','৪। বস্তা, ঝুড়ি ও গুদামঘর ইত্যাদি ৫% ফরমালিন দিয়ে শোধন করতে হবে৷','৫। প্রতি কেজিতে ২ গ্রাম হিসেবে টেকটো ২% গুড়া দিয়ে আলু শোধন করতে হবে৷'));


insert into cropDisease (name, disease, symptom, cure) values ('আলু', 'আলুর নরম পচা রোগ', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ: ', ' ',  '১। আক্রান্ত অংশের কোষ পচে যায়৷', '২। পচা আলুতে এক ধরনের উগ্র গন্ধের সৃষ্টি হয়৷ চাপ দিলে আলু থেকে এক প্রকার দূষিত পানি বেরিয়ে আসে৷','৩। আক্রান্ত অংশ ঘিয়ে রংয়ের নরম হয় যাকে সহজেই সুস্থ অংশ থেকে আলাদা করা যায়৷'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। সুস্থ ও রোগমুক্ত বীজ ব্যবহার করতে হবে৷', '২। অতিরিক্ত সেচ পরিহার করতে হবে৷','৩। উচ্চ তাপ এড়ানোর জন্য আগাম চাষ করতে হবে৷','৪। ভালোভাবে বাছাই করে আলু সংরক্ষণ করতে হবে৷','৫। ১% ব্লিচিং পাউডার অথবা ৩% বরিক এসিডের দ্রবণে টিউবার শোধন করে বীজ আলু সংরক্ষণ করতে হবে৷'));








insert into cropDisease (name, disease, symptom, cure) values ('টমেটো', 'নাবি ধ্বসা রোগ:', CONCAT_WS(CHAR(10 using utf8), 'লক্ষণ :', ' ', '১। এটি একটি ছত্রাকজনিত রোগ। গাছের আক্রান্ত অংশ, বীজ, মাটি ও পানি দ্বারা এ রোগ বিস্তার লাভ করে। এ রোগের আক্রমণে টমেটো গাছের পাতায় বিভিন্ন আকারের ভেজা বাদামি বর্ণের দাগ পড়ে। দাগগুলো তাড়াতাড়ি বাড়তে থাকে।৷', '২। পাতা পচে যায়।', '৩। শুষ্ক ও আর্দ্র আবহাওয়ায় পাতার পচন দ্রুত বিস্তার লাভ করে।','৪। শুষ্ক ও আর্দ্র আবহাওয়ায় পাতার পচন দ্রুত বিস্তার লাভ করে।', '৫। টমেটো ফল আক্রান্ত হলে তাতে ধূসর বর্ণের দাগ পড়ে।', '৬। ৯১ থেকে ১০০ ভাগ আপেক্ষিক আর্দ্রতা ও ১২ থেকে ১৩ ডিগ্রি সেলসিয়াস তাপমাত্রায় এ রোগের ছত্রাক দ্রুত বিস্তার লাভ করে।'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। ফসলের পরিত্যক্ত অংশ পুড়িয়ে ফেলতে হবে।', '২। নীরোগ বীজ ব্যবহার করতে হবে।', '৩। শস্য পর্যায় অনুসরণ করতে হবে।','৪। রোগের লক্ষণ দেখার সঙ্গে সঙ্গে একরোবেট এমজেড (ম্যানকোজেব+ডাইমেথোমরফ) ০৪গ্রাম/লিটার বা হেডলাইন টিম (পাইরাক্লস্ট্রাবিন+ডাইমেথোমরফ) ২.৫০গ্রাম/লিটার বা হেমেনকোজেব বা রিডোমিল গোল্ড বা ইন্ডোফিল এম-৪৫ বা ডায়থেন এম-৪৫ প্রভৃতি ০৪গ্রাম/লিটার হারে পানিতে মিশিয়ে ৭ থেকে ১০ দিন অন্তর ¯েপ্র করতে হবে।', '৫। রোগের প্রকোপ বেশি হলে উপরের ছত্রাকনাশকগুলোর সাথে কার্বেন্ডাজিম যেমন- ডিফেন্স ৩৫এসসি বা নোইন বা অটোস্টিন প্রভৃতি মিশাতে হবে।'));


insert into cropDisease (name, disease, symptom, cure) values ('টমেটো', 'আগাম ধ্বসা রোগ:', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ', ' ',  '১। এটিও একটি ছত্রাকজনিত রোগ। গাছের আক্রান্ত অংশ ও বীজের মাধ্যমে এ রোগ বিস্তার লাভ করে।', '২। অত্যধিক শিশির, ঘনঘন বৃষ্টি এবং ২৩ থেকে ২৯ ডিগ্রি সেলসিয়াস তাপমাত্রায় এ রোগ দ্রুত বিস্তার লাভ করে।', '৩। এ রোগের আক্রমণে পুরনো পাতায় বাদামি বা ধূসর বাদামি বর্ণের গোল চক্রাকার দাগ পড়ে। পরে অন্য পাতায় এ রোগ ছড়িয়ে পড়ে।','৪। পাতার কিনারা বিবর্ণ হয়ে যায় এবং কিছুদিনের মধ্যে পাতা ও গাছ মারা যায়।'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। ফসলের পরিত্যক্ত অংশ পুড়িয়ে ফেলতে হবে।', '২। নীরোগ বীজ ব্যবহার করতে হবে।', '৩। শস্য পর্যায় অনুসরণ করতে হবে।','৪। রোগের লক্ষণ দেখার সঙ্গে সঙ্গে একরোবেট এমজেড (ম্যানকোজেব+ডাইমেথোমরফ) ০৪গ্রাম/লিটার বা হেডলাইন টিম (পাইরাক্লস্ট্রাবিন+ডাইমেথোমরফ) ২.৫০গ্রাম/লিটার বা হেমেনকোজেব বা রিডোমিল গোল্ড বা ইন্ডোফিল এম-৪৫ বা ডায়থেন এম-৪৫ প্রভৃতি ০৪গ্রাম/লিটার হারে পানিতে মিশিয়ে ৭ থেকে ১০ দিন অন্তর ¯েপ্র করতে হবে।', '৫। অথবা ডিফেন্স ৩৫এসসি বা কোগার ২৮এসসি বা এমিস্টার টপ নামক ছত্রাকনাশক ¯েপ্র করতে হবে। ছত্রাকনাশকের মাত্রা হবে প্রতি লিটার পানিতে ০১ মিলি।'));


insert into cropDisease (name, disease, symptom, cure) values ('টমেটো', 'ছত্রাকজনিত ঢলে পড়া রোগ:', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ: ', ' ',  '১। চারা অবস্থায় এ রোগ দেখা যায়।', '২। কয়েক ধরনের ছত্রাকের আক্রমণে এ রোগ হয়।','৩। বীজ ও মাটি দ্বারা এ রোগ বিস্তার লাভ করে।','৪। টমেটো গাছের গোড়া ও শিকড়ে বাদামি বর্ণের দাগ পড়ে।','৫। টমেটো গাছের গোড়া ও শিকড়ে বাদামি বর্ণের দাগ পড়ে।'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। রোগ সহনশীল জাতের চাষ।', '২। শস্য পর্যায় অনুসরণ।','৩। রোগ দেখা দেয়ার সঙ্গে সঙ্গে ডিফেন্স ৩৫ এসসি নামক ছত্রাকনাশক ¯েপ্র করতে হবে। ছত্রাকনাশকের মাত্রা হবে প্রতি লিটার পানিতে ০১ মিলি।'));


insert into cropDisease (name, disease, symptom, cure) values ('টমেটো', 'ব্যাকটেরিয়াল উইল্ট বা নেতিয়ে পড়া:', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ: ', ' ',  '১। মাটিতে বসবাসকারী এক ধরনের ব্যাকটেরিয়া দ্বারা এ রোগ ছড়ায়।', '২। গাছের ক্ষতের মধ্য দিয়ে এ রোগের জীবাণু ঢুকে পড়ে।','৩। আক্রান্ত গাছের পাতা ঢলে পড়ে এবং গাছ মারা যায়।'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। আক্রান্ত গাছ তুলে পুড়িয়ে ফেলা।', '২। শস্য পর্যায় অনুসরণ।','৩। স্টেপটোমাইসিন বা ব্যাকট্রোবান নামক ব্যাকটেরিয়ানাশক ¯েপ্র করা। প্রয়োগমাত্রা প্রতি লিটার পানিতে ০৪ গ্রাম।'));


insert into cropDisease (name, disease, symptom, cure) values ('টমেটো', 'মোজাইক রোগ:', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ: ', ' ',  '১। এটি টমেটোর একটি ভাইরাসজনিত রোগ।', '২। বীজ ও কীটপতঙ্গের মাধ্যমে এ রোগ বিস্তার লাভ করে।','৩। এ রোগের আক্রমণে পাতায় হলুদ ও সবুজ রঙের ছোট ছোট দাগ পড়ে।','৪। রোগের আক্রমণে গাছের বৃদ্ধি কমে যায় এবং ফলন হ্রাস পায়।'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। নীরোগ বীজ ব্যবহার।', '২। পোকা দমন। আক্রান্ত গাছ তুলে ফেলা। পোকা দমনের জন্য হলুদ আঠালো ফাঁদ ব্যবহার।'));


insert into cropDisease (name, disease, symptom, cure) values ('টমেটো', 'পাতা কোঁকড়ানো রোগ:', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ: ', ' ',  '১। এটি একটি ভাইরাসজনিত রোগ।', '২। এক ধরনের সাদা মাছি দ্বারা এ রোগ ছড়ায়।','৩। পাতার ফলক কিনার থেকে মধ্য শিরার দিকে গুটিয়ে বা কুঁকড়িয়ে যায়।','৪। পাতা খসখসে হয়ে ওঠে এবং হলুদ হয়ে যায়। গাছের বৃদ্ধি ব্যাহত হয়।'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। রোগ প্রতিরোধী জাতের চারা লাগানো', '২। রোগ প্রতিরোধী জাতের চারা লাগানোর পর নোভাস্টার ৫৬ ইসি ২ থেকে ৩ বার প্রতি লিটার পানিতে ০২ মিলি মিশিয়ে ¯েপ্র করতে হবে।','৩। আক্রান্ত গাছ তুলে পুড়িয়ে ফেলতে হবে।', '৪। নীরোগ বীজ ব্যবহার করতে হবে।', '৫। হলুদ আঠালো ফাঁদ ব্যবহার করতে হবে।'));


insert into cropDisease (name, disease, symptom, cure) values ('টমেটো', 'শিকড়ের গিঁট রোগ:', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ: ', ' ',  '১। এটি একটি নেমাটোড বা কৃমিজনিত রোগ।', '২। এর আক্রমণে টমেটো গাছের শিকড়ে ছোট ছোট গিঁট দেখা যায়।','৩। গিঁটগুলো আস্তে আস্তে বড় হয়।','৪। আক্রান্ত গাছের বাড়তি কমে যায় এবং শিকড় পচে গাছ মারা যায়।'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। শস্য পর্যায় অনুসরণ করা।', '২। আক্রান্ত জমিতে একরপ্রতি ১৬ কেজি হারে ফুরাডান ৫জি ব্যবহার করতে হবে।','৩। জমিতে জো থাকা অবস্থায় গাছের গোড়া থেকে চার-পাঁচ ইঞ্চি দূরে লাঙলের ভাউর টেনে তাতে ফুরাডান ৫জি প্রয়োগ করে মাটি দিয়ে ঢেকে দিতে হবে।'));