CREATE TABLE `potatoProduction` (
  `introduction` longtext DEFAULT NULL,
  `selectcSpecies` longtext DEFAULT NULL,
  `nativeSpecies` longtext DEFAULT NULL,
  `hybridSpecies` longtext DEFAULT NULL,
  `growingMethod` longtext DEFAULT NULL,
  `selectSoil` longtext DEFAULT NULL,
  `growingSeason` longtext DEFAULT NULL,
  `seedPreparation` longtext DEFAULT NULL,
  `seedSize` longtext DEFAULT NULL,
  `seeding` longtext DEFAULT NULL,
  `seedRate` longtext DEFAULT NULL,
  `climate` longtext DEFAULT NULL,
  `fertilizerQuantity` longtext DEFAULT NULL,
  `fertilizerMethod` longtext DEFAULT NULL,
  `irrigation` longtext DEFAULT NULL,
  `care` longtext DEFAULT NULL,
  `maturity` longtext DEFAULT NULL,
  `collect` longtext DEFAULT NULL,
  `coldStorage` longtext DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci


CREATE TABLE `potatoDisease` (
    `name`longtext DEFAULT NULL,
    `symptom` longtext DEFAULT NULL,
    `cure` longtext DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci


insert into potatoProduction(introduction, selectcSpecies, nativeSpecies, hybridSpecies, growingMethod, selectSoil, growingSeason, seedPreparation, seedSize, seeding, seedRate, climate, fertilizerQuantity, fertilizerMethod, irrigation, care, maturity, collect, coldStorage) values(CONCAT_WS(CHAR(10 using utf8), 'আলু বিশ্বের অন্যতম প্রধান ফসল। উৎপাদনের দিক থেকে ধান, গম ও ভুট্টার পরেই চতুর্থ স্থানে আছে আলু। বাংলাদেশে আলু একটি গুরুত্বপূর্ণ ফসল। বাংলাদেশের সর্বত্রই এর চাষ হয়ে থাকে। অনুকূল আবহাওয়া ও বাজারজাতকরণের জন্য কিছু জেলায় এর চাষ ব্যাপকভাবে হয়ে থাকে।', 'বাংলাদেশে আলু সাধারণত সবজি হিসেবে খাওয়া হয়। বিভিন্ন তরকারির সাথে খেতে খুবই মুখরোচক। প্রক্রিয়াজাত আলু বিদেশে রপ্তানি করে প্রচুর বৈদেশিক মুদ্রা অর্জন করা সম্ভব। আলু একটি স্টার্চ প্রধান খাদ্য এবং ভাতের বিকল্প হিসেবে খাওয়া যেতে পারে। পৃথিবীর অন্তত ৪০টি দেশে আলু মানুষের অন্যতম প্রধান খাদ্য। আলু একটি স্বল্পমেয়াদি উচ্চফলনশীল ফসল যা জমির স্বল্পতাহেতু বাংলাদেশে খাদ্য উৎপাদন বাড়াতে সহায়তা করতে পারে।', 'বর্তমানে বাংলাদেশ আলু হেক্টরপ্রতি গড় ফলন মাত্র ১১ টন। আলুর উৎপাদন ২০ টন পর্যন্ত বাড়ানো সম্ভব। ফলন বাড়লে উৎপাদন খরচ কমে আসবে। ভাতের বদলে আলু খেলে চালের ওপর বাড়তি চাপ কমে আসবে। বিশেষ করে ফেব্রুয়ারি থেকে জুন পর্যন্ত ভাতের বদলে যদি আলু মাঝে মাঝে খাওয়া হতো তাহলে চালের ওপর নির্ভরতা অনেক কমে যেত।'), CONCAT_WS(CHAR(10 using utf8), 'আলুর জাত নির্বাচন:', ' ', 'ভালো জাতের আলুর চাষ করলে একদিকে চাষি আর্থিকভাবে লাভবান হতে পারে আবার অন্যদিকে ফলনও আশানুরূপ পাওয়া যায়। তাই জাত নির্বাচন অবশ্যই গুরুত্বসহকারে বিবেচনা করতে হবে। বাংলাদেশে যেসব জাতের আলুর চাষ হয়ে থাকে তা হলো দেশি জাত এবং উচ্চফলনশীল উন্নত জাত। বর্তমানে আলু চাষের মোট জমির শতকরা ৬৫ ভাগ জমিতে উন্নত জাতের আলু এবং ৩৫ ভাগ জমিতে দেশি জাতের আলুর চাষ হয়ে থাকে।'), CONCAT_WS(CHAR(10 using utf8),'দেশি জাত:', ' ', 'ফলন কম হলেও দেশি জাতের বৈশিষ্ট্য হলো দীর্ঘদিন ঘরে রেখে খাওয়া যায়। দেশি জাতের আলু ছোট ও ওজন  ৫ থেকে ৪৮ গ্রাম। কিছু দেশি জাত আছে যা উন্নত জাতের চেয়ে ভালো। দেশি জাতের আলু তুলনামূলকভাবে খেতে খুব সুস্বাদু। বর্তমানে বাজারমূল্যে উন্নত জাতের চেয়ে দেশি জাতের আলু বেশি দামে বিক্রি হয়। দেশি জাতসমূহের মধ্যে আউশা, চল্লিশা, দোহাজারী লাল, ফেইন্তাশীল, হাসরাই, লাল পাকরী, লালশীল, পাটনাই, সাদা গুটি শীল বিলাতী ও সূর্যমূখী। দেশি জাতগুলো বিভিন্ন রোগে আক্রান্ত হয়। দেখা গেছে ভাইরাস দ্বারা আক্রান্ত হয়ে দেশি জাতের আলুর ফলন কমে যায়। বীজের মাধ্যমেই এ রোগটি ছড়িয়ে থাকে। তাই দেশি জাত নির্বাচনের ক্ষেত্রে রোগমুক্ত বীজ সংগ্রহ করে লাগানো উচিত।'), CONCAT_WS(CHAR(10 using utf8),'উচ্চফলনশীল:', ' ', '১৯৬০ সাল থেকে এ পর্যন্ত যেসব উন্নত জাতের আলুর চাষ হচ্ছে তার মধ্যে হিরা, আইলসা, পেট্রোনিস, মুল্টা, ডায়ামন্ট, কার্ডিনাল, মন্ডিয়াল, কুফরী সিন্দুরী, চমক, ধীরা, গ্রানোলা, ক্লিওপেট্রা ও চিনেলা জাতটি সবচেয়ে বেশি চাষ হয়েছে। বারি টিপিক্রস-১ এবং বারি টিপিক্রস-২ নামে ২টি হাইব্রিড জাতের আলু বাংলাদেশ কৃষি গবেষণা ইনস্টিটিউট থেকে উদ্ভাবন করা হয়েছে। এছাড়াও বারি আলু-১ (হীরা), বারি আলু-৪ (আইলসা), বারি আলু-৭ (ডায়ামন্ট), বারি আলু-৮ (কার্ডিনাল), বারি আলু-১১ (চমক), বারি আলু-১২ (ধীরা), বারি আলু-১৩ (গ্রানোলা), বারি আলু-১৫ (বিনেলা), বারি আলু-১৬ (আরিন্দা), বারি আলু-১৭ (রাজা), বারি আলু-১৮ (বারাকা), বারি আলু-১৯ (বিন্টজে) এবং বারি আলু-২০ (জারলা) জাত রয়েছে। এসব জাত বাংলাদেশ কৃষি গবেষণা থেকে উদ্ভাবিত। এগুলো সবই উচ্চফলনশীল জাত।'), CONCAT_WS(CHAR(10 using utf8),'উৎপাদন পদ্ধতি:', ' ', 'বাংলাদেশের কৃষক আলু উৎপাদনের জন্য বিভিন্ন পদ্ধতি অবলম্বন করে থাকেন। কিন্তু এগুলো বিজ্ঞানসম্মত নয়। তাই বিজ্ঞানসম্মত উপায়ে চাষ করলে একদিকে যেমন কৃষক লাভবান হবে অন্যদিকে ফলনও বাড়বে।'), CONCAT_WS(CHAR(10 using utf8),'মাটি নির্বাচন:', ' ', 'দোআঁশ ও বেলে-দোআঁশ মাটি আলুর জন্য সব চাইতে উপযোগী৷ এটেল-দোআঁশ মাটিতেও আলুর চাষ করা যায়, তবে এই রকম মাটিতে আলু খুব একটা ভালো হয় না৷ আলুর মাটি সুনিষ্কাশনযুক্ত, গভীর ও কিছুটা অম্লাত্মক হওয়া চাই৷ PH ৫.৫-৬০ এর মধ্য হওয়া বাঞ্ছনীয়, এতে আলুর জন্য ক্ষতিকর রোগ স্কেভিস হওয়ার সম্ভাবনা থাকে না৷'),CONCAT_WS(CHAR(10 using utf8),'উৎপাদন মৌসুম:', ' ', 'বাংলাদেশে সাধারণত নভেম্বর মাসের আগে আলু লাগানো যায় না, কারণ তার আগে জমি তৈরি সম্ভব হয় না। নভেম্বরের পরে আলু লাগালে ফলন কমে যায়। এ জন্য উত্তরাঞ্চলে মধ্য-কার্তিক (নভেম্বর প্রথম সপ্তাহ), দক্ষিণাঞ্চলে অগ্রহায়ণ ১ম সপ্তাহ থেকে ২য় সপ্তাহ (নভেম্বর মাসের মধ্য থেকে শেষ সপ্তাহ)।'), CONCAT_WS(CHAR(10 using utf8),'আলুর চাষাবাদ পদ্ধতি এর অন্যতম বিষয় হলো বীজ বপন পূর্বে করণীয়গুলো জানা :', ' ', 'বীজ প্রত্যয়িত হলে বপনের আগে আর কোনো রকম ব্যবস্থা নেওয়ার প্রয়োজন হয় না৷ অন্যথায় বীজ শোধন করে নেওয়া ভালো৷ বীজ শোধনের জন্য মারকিউরিক ক্লোরাইড (Mercuric chloride), ফার্মালডিহাইড (Formaldehyde) অথবা, ইয়েলো অক্সাইড অব মার্কারি (Yellow oxide of mercury) ব্যবহার করা যায়৷ উক্ত তরল গুলোতে আলুর বীজকে কিছুক্ষণ চুবিয়ে উঠিয়ে নিলেই বীজ শোধন হয়ে যায়৷ তবে কাটা বীজ ব্যবহার করতে গেলে কিছু সাবধানতা অবলম্বন করতে হবে, যেমন-', '১। বীজ কাটার সময় ধারালো ছুড়ি দিয়ে লম্বালম্বি কাটতে হবে।','২। বীজের কাটা দিকটায় পরিষ্কার ঠাণ্ডা ছাই লাগিয়ে দিতে হবে৷ এই সমস্ত সাবধানতা অবলম্বন করলে বীজ পচে নষ্ট হয়ে যাবার সম্ভাবনা কম থাকবে৷', '৩। কাটা বীজ আলুকে কিউরিং করে নিলেও উপকার পাওয়া যায়৷ কিউরিংয়ের জন্য কর্তিত বীজ আলুকে ৬০-৭০ডিগ্রি ফারেনহাইট অর্থাৎ ১৬-২১ ডিগ্রি সেলসিয়াস তাপমাত্রা ও ৮৫-ঌ০% আদ্রতায় সপ্তাহ খানেক রাখলে বীজের কর্তিত তলে একটি আত্নরক্ষাকারী স্তর সৃষ্টি হয় যা বীজের মধ্যে রোগজীবাণুর প্রবেশ অনেকাংশে রোধ করতে পারে৷ তবে এইভাবে কিউরিং করা সাধারণ চাষীদের পক্ষে সম্ভব না হলেও বীজ আলুকে পাতলা স্তরে মেঝেতে ছড়িয়ে ভিজা চট দ্বারা কয়েকদিন ঢেকে রাখলে সুফল পাওয়া যাবে৷'),CONCAT_WS(CHAR(10 using utf8),'বীজের আকার:', ' ', '২৫-৩৫ গ্রাম ওজনের বীজ রোপণ করা সবদিক থেকে ভালো।'), CONCAT_WS(CHAR(10 using utf8),'বীজ বপন:', ' ', 'বপনের জন্য আলুর টিউবার অর্থাৎ কন্দ ব্যবহার করা হয়৷ আগের বছরের সুরক্ষিত অঙ্কুরিত বীজ ব্যবহার করা হয়৷ আশ্বিনের মাঝামাঝি হতে অগ্রাহয়ণের মাঝামাঝি পর্যন্ত আলু লাগানো যেতে পারে৷ তবে আগাম ফসল করতে হলে ভাদ্র মাসের শেষে বীজ বপন করতে হবে৷','আলু বীজ সারিতে বপন করতে হয়৷ এক সারি হতে অন্য সারির দূরত্ব ৬০ সেন্টিমিটার রাখতে হবে, এবং সারিতে এক বীজ হতে অন্য বীজের দূরত্ব হবে ২৩-৩৮ সেন্টিমিটার৷ বীজ আস্ত বপন করাই ভালো, তবে আকারে বেশি বড় হলে কেটে দুইভাগ করে লাগান যায়৷ যে বীজের ব্যাস ২ হতে ৩ সেন্টিমিটার সেই বীজই বপনের জন্য উত্তম এবং সেসব বপন বপন করার সময় কাটার প্রয়োজন হয় না৷','আর যেসব বীজের ব্যাস এক সেন্টিমিটারের চেয়ে বেশি সেগুলো কেটে লাগানো যেতে পারে৷ কেটে বপন করলে বীজের পরিমাণ কম লাগে অর্থাৎ আস্ত বীজ ব্যবহার করলে যদি এক প্রতি হেক্টরে ১৫০০ কেজি লাগে সেক্ষেত্রে কর্তিত বীজ ব্যবহার করলে এর অর্ধেক অর্থাৎ ৭৫০ কেজি বীজ লাগবে৷','সারিতে দুই পদ্ধতিতে বীজ বপন করা যায়:','প্রথম পদ্ধতিতে প্রতি সারি বরাবর ৫-৭ সেন্টিমিটার মাটি সরিয়ে নিয়ে নালা প্রস্তুত করা হয়, তারপর সেই নালাতে নির্দিষ্ট দূরত্বে বীজ বপন করে মাটি দ্বারা বীজ ঢেকে দেওয়া হয়৷','দ্বিতীয় পদ্ধতিতে সারির মাটি না খুড়ে অর্থাৎ নালা না করে সারির দাগ বরাবর বীজ নির্দিষ্ট ব্যবধানে বপন করার পর দুই সারির মধ্যবর্তী জায়গা হতে মাটি টেনে উঁচু করে বীজ ঢেকে দেওয়া হয়৷','শেষোক্ত পদ্ধতিটিই বিজ্ঞানসম্মত, কেননা এতে জমির রস অধিক দিন বজায় থাকে এবং সারির মাটি আলগা থাকে বলে আলুর কন্দ বৃদ্ধিতে কোনো বাধার সৃষ্টি হয় না৷ অন্য দিকে প্রথমোক্ত পদ্ধতিতে নালার মাটি চাপ খাওয়া থাকে বলে স্কন্দের বৃদ্ধি বাধাগ্রস্ত হয়৷ ফলে কন্দ অর্থাৎ আলুর আকার ছোট থেকে যায়৷'),CONCAT_WS(CHAR(10 using utf8),'বীজের হার:', ' ', 'প্রতি হেক্টরে ১.৫ টন। রোপণের দূরত্ব ৬০x২৫ সে. মি. (আস্ত আলু) এবং ৪৫x১৫ সে. মি. (কাটা আলু)। কৃষকেরা ঘরে সংরক্ষিত দেশি জাতের যে বীজ ব্যবহার করেন তা খুবই নিকৃষ্টমানের। কোনো কোনো সময় হিমাগারে থাকা অবস্থায় আলুর মাঝখানে কাল দাগ দেখা যায়। মাঠে থাকা অবস্থায় বা সংরক্ষণের সময় যদি উচ্চ তাপমাত্রায় (৩৫ ডিগ্রি সে. এর উপরে) থাকলে বা অক্সিজেন বিহীন অবস্থায়  থাকলে এমনটি হয়। এ রোগটিকে ব্লাকহার্ট রোগ বলে। আবার যদি হিমাগারের তাপমাত্রা ২ ডিগ্রি সে. এর নিচে চলে যায় তাহলে আলু শীতলাঘাতে ক্ষতিগ্রস্ত হয়। এ ধরনের আলু গজাবে না। তাই কৃষক অবশ্যই এদিকটা বিবেচনা করে আলু বীজ সংগ্রহ করবেন।'),CONCAT_WS(CHAR(10 using utf8),'জলবায়ু', ' ', 'আলু চাষের জন্য তাপমাত্রা ও আলোর প্রভাব খুবই প্রকট, দেখা গেছে ১৫ ডিগ্রি - ২০ ডিগ্রি সে. গড় তাপমাত্রা আলু চাষের জন্য খুবই উপযোগী। ২০ ডিগ্রি সে. তাপমাত্রার ওপরে গেলে ফলন কমতে থাকে আবার ৩০ ডিগ্রি সে. এ আলু উৎপাদন ক্ষমতা লোপ পায়। আবার ১০ ডিগ্রি সে. তাপমাত্রার নিচে গেলে গাছের বৃদ্ধি কমে যায়। এজন্য আলু লাগানোর সময় ২০ ডিগ্রি - ২৫ ডিগ্রি সে. তাপমাত্রা থাকে সেদিকে লক্ষ্য রাখতে হবে। এ তাপমাত্রায় গাছ দ্রুত গজায়। আবার বাংলাদেশে দেখা গেছে যে বছর মেঘমুক্ত আকাশ ও তাপমাত্রা সঠিকভাবে থাকে সে বছর আলুর গড় ফলন ১০-১৫% বেড়ে যায়।'),CONCAT_WS(CHAR(10 using utf8), 'সারের পরিমাণ:', ' ', 'কৃষকেরা যদি আলুর উচ্চফলন পেতে চান তাহলে সুষম সারের বিকল্প নেই। সাধারণ কৃষকের জন্য আলু চাষে নিম্নোক্ত হারে সার ব্যবহার করা প্রয়োজন।', 'ইউরিয়া            -   ২২০-২৫০ কেজি/হেক্টর', 'টিএসপি            -   ১২০-১৫০ কেজি/হেক্টর','এমওপি             -  ২২০-২৫০ কেজি/হেক্টর','জিপসাম            -   ১০০-১২০ কেজি/হেক্টর','জিংক সালফেট     -   ৮-১০ কেজি/হেক্টর','ম্যাগনেসিয়াম সালফেট - ৮০-১০০ কেজি/হেক্টর','(অম্লীয় বেলে মাটির জন্য)','বোরন             -   ৮-১০ কেজি/হেক্টর','গোবর             -   ৮-১০ টন/হেক্টর','জমিতে যদি সবুজ সার প্রয়োগ করা হয় তাহলে গোবরের প্রয়োজন নেই।'), CONCAT_WS(CHAR(10 using utf8),'সার প্রয়োগ পদ্ধতি', ' ', 'গোবর, অর্ধেক ইউরিয়া, টিএসপি, এমওপি, জিপসাম ও জিংক সালফেট আলু রোপণের আগেই মাটির সাথে মিশিয়ে দিতে হবে। বাকি অর্ধেক ইউরিয়া ৩০-৩৫ দিন পর অর্থাৎ দ্বিতীয় বার মাটি তোলার সময় প্রয়োগ করতে হবে৷ অম্লীয় বেলে মাটির জন্য ৮০-১০০ কেজি/হেক্টর ম্যাগনেসিয়াম সালফেট এবং বেলে মাটির জন্য বোরন ৮-১০ কেজি/হেক্টর প্রয়োগ করলে ভালো ফলন পাওয়া যায়৷'),CONCAT_WS(CHAR(10 using utf8),'সেচ', ' ', 'আলু শীতকালীন সবজি। আর শীতকাল শুষ্ক এজন্য আলু চাষে সেচের প্রয়োজন হয়। পানির প্রাপ্যতা কম হলে আলুর ফলন মারাত্মকভাবে কমে যায়। গবেষণায় দেখা গেছে, বীজ আলু বপনের ২০-২৫ দিনের মধ্যে একবার সেচ দিতে হবে। ৪০-৪৫ দিনের মধ্যে দ্বিতীয় সেচ এবং ৬০-৬৫ দিনের মধ্যে আরেকটি সেচ দিতে হবে। তবে দেশের উত্তরাঞ্চলে ৮-১০ দিন পর সেচ দিলে ফলন বেশি পাওয়া যায়।'),CONCAT_WS(CHAR(10 using utf8),'পরিচর্যা', ' ', 'বীজ বপনের ৬০ দিন পর্যন্ত আলুর ক্ষেত আগাছা মুক্ত রাখতে হয়৷ আলুর জমিতে আগাছা দমন আলাদাভাবে করার প্রয়োজন পড়ে না৷ গাছের গোড়ায় মাটি তুলে দেয়া ও গোঁড়ার মাটি আলগা করে দেয়ার সময়ই আগাছা দমন হয়ে যায়৷','আলু লাগানোর ৩০-৩৫ দিন পর গোড়ায় মাটি দেওয়া প্রয়োজন৷ জমিতে আলুর গাছ যখন ৫-৬ ইঞ্চি অর্থাৎ ১২-১৫ সেন্টিমিটার হয় তখন দুই সারির মাঝখানের মাটি হালকাভাবে কুপিয়ে নরম ঝুরঝুরা করে নিতে হয়৷ এই সময় জমির আগাছা নিধনের কাজও হয়ে যায়৷','ঝুরঝুরা মাটি কোদালি দ্বারা টেনে সারিতে গাছের দুই দিকে দেওয়া হয়৷ এর তিন সপ্তাহ পর গোড়ায় আবার মাটি দিতে হয়৷ গাছের বৃদ্ধি বেশি হলে আর একবার অর্থাৎ তৃতীয়বারের মতো মাটি দেওয়া হয়৷ গাছের গোড়ায় এইভাবে মাটি দিলে গাছের স্টোলনগুলো টিউবার অর্থাৎ আলুতে পরিণত হবার সুযোগ পায়৷ মাটি ঠিকমতো দেওয়া না হলে বর্ধিষ্ণু আলু মাটির বাইরে এসে সবুজ রং ধারণ করে৷ এ রকম আলু খাবার অনুপোযোগী এবং কখনো কখনো তা বিষাক্তও হতে পারে৷'),CONCAT_WS(CHAR(10 using utf8),'পরিপক্বতার লক্ষণ', ' ', 'যখন দেখা যাবে আলু গাছগুলো হলুদ হয়ে মরে যাচ্ছে তখনই বুঝতে হবে আলু তোলার উপযুক্ত সময় হয়েছে৷ সাধারণত আলুবীজ লাগাবার ৩ থেকে ৪ মাসের মধ্যেই এ আলু তোলা যায়৷'),CONCAT_WS(CHAR(10 using utf8),'ফসল সংগ্রহ ও সংরক্ষণ', ' ', 'আলুর সারিতে কোদালের সাহয্যে বা লাঙল চালিয়ে আলু মাটি থেকে তোলা হয়৷ তবে আলু তোলার সময় সতর্ক থাকতে হবে যাতে আলু কেটে বা থেতলিয়ে না যায়, কেননা আলু থেতলিয়ে গেলে সংরক্ষণ করার সময় পচে যায়৷'),CONCAT_WS(CHAR(10 using utf8),'হিমাগারে আলু সংরক্ষণ', ' ', 'গোল আলু সংরক্ষণের এটি একটি আধুনিক ব্যবস্থাপনা৷ এ ব্যবস্থায় খাবার ও বীজ এই দুই শ্রেণীর আলুই দীর্ঘদিন ধরে সংরক্ষণ করা যায়৷ হিমাগার বা কোল্ড স্টোরেজ ইট দ্বারা নির্মিত একটি দালান ঘর যাতে শীতলতা ও আদ্রতা নিয়ন্ত্রণ করা যায়৷ এজন্য এই ঘরটি এমনভাবে তৈরি করা হয় যেন এর দেয়াল ও ছাদে কোথায়ও কোনো ফাঁক না থাকে; তবে  এমন নিয়ন্ত্রিত ফাঁক থাকে যার মাধ্যমে ভিতরের গরম আবহাওয়া শোষক পাখার সাহায্যে বের করে দেওয়া যায়৷৷','হিমাগারের তাপমাত্রা বীজ আলুর বেলা ৩৮-৪০ ফারেনহাইট (৩.৩-৪.৪ ডিগ্রি সে.) এবং খাবার আলুর জন্য ৫৫-৬০ ডিগ্রি ফারেনহাইট (১২.৭-১৫.৫ সে.) হওয়া বাঞ্ছনীয়৷ হিমাগারের অভ্যন্তরে বাতাসের আদ্রতা ৮০%-ঌ০% হবে৷ আলু সংরক্ষণের জন্য হিমাগারকে কয়েকটি কক্ষে বিভক্ত করা হয়৷ প্রতিটি কক্ষে উপর্যুপরি কয়েকটি মাচান (বাঁশের অথবা কাঠের) তৈরি করা হয় যাতে অধিক আলু গুদামজাত করা যায়৷','মাচানে আলু গাদা করে অথবা বস্তায় ভর্তি করে রাখা যায়৷ আলু গাদা  করে রাখলে গাদার উচ্চতা কখনো যেন তিন মিটারের বেশি না হয়৷ আলু বস্তায় রাখলে বস্তা মাচানে খাড়া করে রাখতে হবে এবং বস্তা এমনভাবে পাতলা করে রাখতে হবে যাতে বস্তার ভিতরে অনায়াসে বায়ু চলাচল করতে পারে৷ আলু হিমাগারে প্রবেশ ও বাহির করবার সময় একটি নিয়ম পালন করা বাঞ্চনীয়৷ হিমাগারে প্রবেশের পূর্বে আলুকে ১৬-১৮ ডিগ্রি সেন্টিগ্রেড তাপমাত্রায় ৪৮-৭২ ঘন্টার জন্য প্রিকুলিং এবং বাইরে আনার পূর্বে প্রিহিটিং করতে হবে৷','এতে দুই অবস্থাতেই অর্থাৎ হিমাগারের ঠাণ্ডা ও বাহিরের গরম পরিবেশে খাপ খাওয়ানোর জন্য আলু সহনশীল হয়ে উঠে এবং তাতে করে আলু নষ্ট হওয়া রক্ষা পায়৷ খাবার আলুর বেলায় লক্ষ্য রাখতে হবে হিমাগারের তাপমাত্রা যেন ৪-৫ ডিগ্রি সেন্টিগ্রেডের নিচে নেমে না আসে, কারণ নিম্ন তাপমাত্রায় আলু স্টার্চ বিয়োজিত হয়ে শ্বেতসারে পরিণত হওয়ার ফলে আলু মিষ্টি হয়ে যায়৷ এইরূপ আলু তখন খাওয়ার উপযোগী থাকে না৷ আবার উচ্চ তাপমাত্রায় আলুতে সহজেই কুঁড়ি জন্মায় যা প্রারম্ভেই উল্লেখ করা হয়েছে৷ এতেও সহজেই আলু খাবার অনুপোযোগী হয়ে পড়ে৷ এই পরিস্থিতিতে উপযুক্ত রাসায়নিক দ্রব্য প্রয়োগ করলে আলুতে আর কুঁড়ি জন্মাতে পারে না৷','কুঁড়ি প্রতিরোধকারী রাসায়নিকগুলো হচ্ছে CIPC, IPPC, TCNB এবং MENA. আলু জমি হতে উঠাবার পূর্বে গাছের পাতায় ম্যালিক হাইড্রাজিড নামক রাসায়নিক প্রয়োগ করলে হিমাগার সংরক্ষণকালে আলুতে কুঁড়ি খুব কম জন্মাতে দেখা যায়। ৩০০০ পিপিএম ম্যালিক হাইড্রাজিড প্রয়োগে আলুতে খুব কম চারা গজায়, অন্যদিকে ২০০০ পিপিএম ম্যালিক হাইড্রাজিড এবং ৯০ দিন পর সিআইপিসি প্রয়োগ করলে আলু অনেকদিন পর্যন্ত ভালো থাকে অর্থাৎ কুড়িবিহীন থাকে।'));



insert into potatoDisease (name, symptom, cure) values ('আলুর কাটুই পোকা', CONCAT_WS(CHAR(10 using utf8), 'লক্ষণ :', ' ', '১। কাটুই পোকার কীড়া বেশ শক্তিশালী, ৪০-৫০ মিলিমিটার লম্বা হয়৷', '২। পোকার উপর পিঠ কালচে বাদামি বর্ণের, পার্শ্বদেশ কালো রেখাযুক্ত এবং বর্ণ ধূসর সবুজ৷ শরীর নরম ও তৈলাক্ত৷', '৩। কাটুই পোকার কীড়া চারা গাছ কেটে দেয় এবং আলুতে ছিদ্র করে আলু ফসলের ক্ষতি করে থাকে৷','৪। পোকার কীড়া দিনের বেলা মাটির নিচে লুকিয়ে থাকে৷ আলুর কাটা গাছ অনেক সময় কাটা গোঁড়ার পাশেই পড়ে থাকতে দেখা যায়৷'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। কাটুই পোকার উপদ্রব খুব বেশি না হলে কাটা আলু গাছ দেখে তার কাছাকাছি মাটি উল্টেপাল্টে কীড়া খুঁজে সংগ্রহ করে মেরে ফেলা উচিৎ৷', '২। কাটুই পোকার উপদ্রব খুব বেশি হলে কীটনাশক ব্যবহার করতে হবে৷ প্রতি লিটার পানির সাথে ক্লোরোপাইরফর (ডারসবান) ২০ ইসি ৫ মিলিমিটার হারে মিশিয়ে গাছের গোড়া ও মাটি স্প্রে করে ভিজিয়ে দিতে হবে৷ আলু লাগানোর ৩০-৪০ দিন পর স্প্রে করতে হবে৷'));


insert into potatoDisease (name, symptom, cure) values ('আলুর সুতলী পোকা', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ', ' ',  '১। আলুর সুতলী পোকার মথ আকারে ছোট, ঝালযুক্ত, সরু ডানা বিশিষ্ট ধূসর বাদামি হয়৷', '২। পূর্ণাঙ্গ কীড়া সাদাটে বা হাল্কা গোলাপী বর্ণের এবং ১৫-২০ মিলিমিটার লম্বা হয়ে থাকে৷', '৩। কীড়া আলুর মধ্যে লম্বা সুড়ঙ্গ করে আলুর ক্ষতি করে থাকে৷','৪। বাংলাদেশে বসতবাড়িতে সংরক্ষিত আলু এ পোকার দ্বারা ক্ষতিগ্রস্ত হয়৷'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। বাড়িতে সংরক্ষিত আলু শুকনা বালি, ছাই, তুষ অথবা কাঠের গুড়ার একটি পাতলা স্তর (আলুর উপরে ০.৫ সেন্টিমিটার) দিয়ে ঢেকে দিতে হবে৷', '২। আলু সংরক্ষণ করার আগে সুতলী পোকা আক্রান্ত আলু বেছে ফেলে দিতে হবে৷'));


insert into potatoDisease (name, symptom, cure) values ('আলুর মড়ক বা নাবী ধ্বসা (লেইট ব্লাইট) রোগ', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ: ', ' ',  '১। প্রথমে পাতা, ডগা ও কাণ্ডে ছোট ভিজা দাগ পড়ে৷', '২। ক্রমে দাগ বড় হয় ও সমগ্র পাতা, ডগা ও কাণ্ডের কিছু অংশ ঘিরে ফেলে৷','৩। বাতাসের আপেক্ষিক আদ্রতা বেশি থাকলে ২-৩ দিনের মধ্যেই জমির অধিকাংশ ফসল আক্রান্ত হয়ে পড়ে৷','৪। ভোরের দিকে  আক্রান্ত পাতার নিচে সাদা পাউডারের মতো ছত্রাক চোখে পড়ে৷','৫। আক্রান্ত ক্ষেতে পোড়াপোড়া গন্ধ পাওয়া যায় এবং মনে হয় যেন জমির ফসল পুড়ে গেছে৷'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। রোগমুক্ত বীজ ব্যবহার করা৷', '২। আক্রান্ত জমিতে সেচ যথাসম্ভব বন্ধ করে দিতে হবে৷','৩। রোগ দেখা দেওয়ার সাথে সাথে রিডোমিল (০.২%), ডাইথেন এম-৪৫ (০.২%) ইত্যাদি   ছত্রাকনাশক অনুমোদিত হারে ১০-১২ দিন পর পর স্প্রে করতে হবে৷'));


insert into potatoDisease (name, symptom, cure) values ('আলুর আগাম ধসা বা আর্লি ব্লাইট রোগ', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ: ', ' ',  '১। নিচের পাতা ছোট ছোট বাদামি রংয়ের অল্প বসে-যাওয়া কৌণিক দাগ পড়ে৷', '২। আক্রান্ত অংশে সামান্য বাদামি এলাকার সাথে পর্যায়ক্রমে কালচে রংয়ের চক্রাকার দাগ অপেক্ষাকৃত লম্বা ধরনের হয়৷','৩। গাছ মরে যাওয়া এ রোগের লক্ষণীয় উপসর্গ৷','৪। আক্রান্ত টিউবারের গায়ে গাঢ় বাদামি থেকে কালচে বসে যাওয়া দাগ পড়ে৷'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। সুষম সার প্রয়োগ এবং সময়মতো সেচ প্রয়োগ নিশ্চিত করতে হবে৷', '২। রোগ দেখা দেওয়ার সাথে সাথে প্রতি লিটার পানিতে ২ গ্রাম রোভরাল মিশিয়ে ৭-১০ দিন পর পর প্রয়োগ করতে হবে৷ ডাইথেন এম-৪৫ ০.২% হারে প্রয়োগ করা যায়৷','৩। আগাম জাতের আলু চাষ করতে হবে৷'));


insert into potatoDisease (name, symptom, cure) values ('আলুর স্টেম ক্যাষ্কার স্কার্ফ রোগ', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ: ', ' ',  '১। গজানো অঙ্কুরের মাথায় এবং স্টোলনে আক্রমণের দাগ দেখা যায়৷', '২। বড় গাছের গোঁড়ার দিকে লম্বা লালচে বর্ণের দাগ বা ক্ষতের সৃষ্টি হয়৷','৩। কাণ্ডের সাথে ছোট ছোট টিউবার দেখা যায়৷','৪। আক্রান্ত টিউবারের গায়ে শক্ত কালচে এবং সুপ্ত রোগ জীবাণু গুটি দেখা যায়৷'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। টেরক্লোর হেক্টরপ্রতি ১৫ কেজি মাত্রায় বীজ লাগানোর পূর্বে বীজ নালায় প্রয়োগ করতে হবে৷', '২। ৩% বরিক এসিড় দ্বারা বীজ শোধন বা স্প্রে যন্ত্রের সাহায্যে প্রয়োগ করলেও ভালো ফল পাওয়া যায়৷','৩। বীজ আলু মাটির বেশি গভীরে রোপণ পরিহার করতে হবে৷','৪। ভালোভাবে অঙ্কুরিত বীজ আলু রোপণ করতে হবে৷'));


insert into potatoDisease (name, symptom, cure) values ('কাণ্ড ও আলু পচা রোগ', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ: ', ' ',  '১। এ রোগের আক্রমণের ফলে বাদামি দাগ কাণ্ডের গোড়া ছেয়ে ফেলে৷', '২। গাছ ঢলে পড়ে এবং পাতা বিশেষ করে নিচের পাতা হলদে হয়ে যায়৷','৩। আক্রান্ত অংশে বা আশেপাশের মাটিতে ছত্রাকের সাদা সাদা জালিকা দেখা যায়৷','৪। কিছু দিন পর সরিষার দানার মত রোগ জীবাণু গুটি বা স্কেলেরোসিয়া সৃষ্টি হয়৷','৫। আলুর গা থেকে পানি বের হয় এবং পচন ধরে৷ ক্রমে আলু পচে নষ্ট হয়ে যায়৷'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। আক্রান্ত গাছ কিছুটা মাটিসহ সরিয়ে ফেলতে হবে৷', '২। জমি গভীরভাবে চাষ করতে হবে৷','৩। জমিতে সব সময় পঁচা জৈবসার প্রয়োগ করতে হবে৷'));


insert into potatoDisease (name, symptom, cure) values ('ঢলে পড়া এবং বাদামি পচন রোগ', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ: ', ' ',  '১। গাছের একটি শাখা বা এক অংশ ঢলে পড়তে পারে৷', '২। পাতা সাধারণত হলুদ হয় না এবং সবুজ অবস্থায়ই চুপসে ঢলে পড়ে৷','৩। গোঁড়ার দিকে গাছের কাণ্ড ফেড়ে দেখলে বাদামি আক্রান্ত এলাকা দেখা যায়৷','৪। ঢলে পড়া গাছ খুব দ্রুত চুপসে যায়৷','৫। আক্রান্ত আলু কাটলে ভিতরে বাদামি দাগ দেখা যায়৷','৬। আলুর চোখে সাদা পুঁজের মতো দেখা যায় এবং আলু অল্প দিনের মধ্যেই পচে যায়৷'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। সুস্থ রোগমুক্ত বীজ ব্যবহার করতে হবে৷', '২। আলু লাগানোর সময় প্রতি হেক্টরে ৮০-ঌ০ কেজি হারে স্ট্যাবল ব্লিচিং পাউডার জমিতে প্রয়োগ করতে হবে৷','৩। পরিমিত মাত্রায় সেচ প্রয়োগ করতে হবে এবং রোগ দেখা দিলে পানি সেচ বন্ধ করে দিতে হবে৷'));


insert into potatoDisease (name, symptom, cure) values ('আলুর দাদ রোগ', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ: ', ' ',  '১। হালকা দাদ হলে টিউবারের উপরে উঁচু এবং ভাসা বিভিন্ন আকারের বাদামি দাগ পড়ে৷', '২। রোগের গভীর দাদে গোলাকার গর্ত বা ডাবা দাগ পড়ে৷','৩। রোগের আক্রমণ সাধারণত ত্বকেই সীমাবদ্ধ থাকে৷'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। রোগের আক্রমণ সাধারণত ত্বকেই সীমাবদ্ধ থাকে৷', '২। জমিতে বেশি মাত্রায় নাইট্রোজেন সার ব্যবহার বর্জন করতে হবে৷','৩। ৩% বরিক এসিড দিয়ে বীজ শোধন করে নিতে হবে৷','৪। জমিতে হেক্টরপ্রতি ১২০ কেজি জিপসাম সার প্রয়োগ করতে হবে৷'));


insert into potatoDisease (name, symptom, cure) values ('আলুর পাতা মোড়ানো রোগ', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ: ', ' ',  '১। আক্রান্ত গাছের পাতা খসখসে, খাড়া ও উপরের দিকে মুড়ে যায়৷৷', '২। আগার পাতার রং হালকা সবুজ হয়ে যায় এবং গাছের বৃদ্ধি বন্ধ হয়ে যায়৷','৩। আগার পাতার রং হালকা সবুজ হয়ে যায় এবং গাছের বৃদ্ধি বন্ধ হয়ে যায়৷','৪। গাছ খাটো হয় এবং সোজা হয়ে উপরের দিকে দাঁড়িয়ে থাকে৷ আলুর সংখ্যা কমে যায়৷'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। রোগমুক্ত বীজ ব্যবহার করা৷', '২। কীটনাশক (এজেড্রিন, নোভাক্রন, মেনোড্রিন ইত্যাদি) ২ মিলিমিটার অথবা ১ মিলিমিটার ডাইমেক্রন প্রতি লিটার পানিতে মিশিয়ে ৭-১০ দিন পর পর জমিতে স্প্রে করতে হবে৷','৩। আক্রান্ত গাছ টিউবারসহ তুলে ফেলতে হবে৷'));


insert into potatoDisease (name, symptom, cure) values ('আলুর মোজাইক রোগ', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ: ', ' ',  '১। পাতায় বিভিন্ন ধরনের ছিটে দাগ পড়ে, পাতা বিকৃত ও ছোট হয়৷', '২। ভাইরাস এবং আলুর জাতের উপর নির্ভর করে লক্ষণ ভিন্নতর হয়৷','৩। ভাইরাস এবং আলুর জাতের উপর নির্ভর করে লক্ষণ ভিন্নতর হয়৷'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। সুস্থ গাছ থেকে বীজ সংগ্রহ করতে হবে এবং রোগমুক্ত বীজ ব্যবহার করতে হবে৷', '২। টমেটো, তামাক এবং কতিপয় সোলানেসি গোত্রভুক্ত আগাছা এ ভাইরাসের বিকল্প পোষক৷  সুতরাং আশেপাশে এ ধরনের গাছ রাখা যাবে না৷ আক্রান্ত গাছ আলুসহ রগিং করে ফেলতে হবে৷','৩। জাব পোকা দমন পদ্ধতি অনুসরণ করতে হবে অর্থাৎ ১ মিলিমিটার ডাইমেক্রন প্রতি লিটার পানিতে মিশিয়ে ৭-১০ দিন পর পর জমিতে স্প্রে করতে হবে৷'));


insert into potatoDisease (name, symptom, cure) values ('আলুর হলদে রোগ', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ: ', ' ',  '১। স্থানীয় জাতের আলুতে এ রোগ বেশি হয়৷', '২। পাতা কুঁচকে যায় ও ছিটা দাগ দেখা যায়৷','৩। দূর থেকে আক্রান্ত গাছ সহজেই চোখে পড়ে৷ আলু ছোট হয় বা একেবারেই হয় না৷'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। রোগমুক্ত বীজ ব্যবহার করতে হবে৷', '২। রগিং করে আক্রান্ত গাছ উঠিয়ে ফেলতে হবে৷','৩। কীটনাশক প্রয়োগ করে পাতা ফড়িং দমন করতে হবে৷ এজন্য ডাইমেক্রন (০.১%) ৭-১০ দিন পর স্প্রে করতে হবে৷','৪। অপেক্ষাকৃত বড় আকারের আলু বীজ হিসেবে ব্যবহার করতে হবে৷'));


insert into potatoDisease (name, symptom, cure) values ('আলুর শুকনো পচা রোগ', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ: ', ' ',  '১। আলুর গায়ে কিছুটা গভীর কালো দাগ পড়ে৷', '২। আলুর ভিতরে গর্ত হয়ে যায়৷','৩। প্রথম পচন যদিও ভিজা থাকে পরে তা শুকিয়ে শক্ত হয়ে যায়৷','৪। আক্রান্ত অংশে গোলাকার ভাঁজ এবং কখনো কখনো ঘোলাটে সাদা ছত্রাক জালিকা দেখা যায়৷'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। আলু ভালোভাবে বাছাই করে সংরক্ষণ করতে হবে৷', '২। যথাযথ কিউরিং করে আলু গুদামজাত করতে হবে৷','৩। ডাইথেন এম-৪৫ দ্রবণ ০.২% দ্বারা বীজ আলু শোধন করতে হবে৷','৪। বস্তা, ঝুড়ি ও গুদামঘর ইত্যাদি ৫% ফরমালিন দিয়ে শোধন করতে হবে৷','৫। প্রতি কেজিতে ২ গ্রাম হিসেবে টেকটো ২% গুড়া দিয়ে আলু শোধন করতে হবে৷'));


insert into potatoDisease (name, symptom, cure) values ('আলুর নরম পচা রোগ', CONCAT_WS(CHAR(10 using utf8),'লক্ষণ: ', ' ',  '১। আক্রান্ত অংশের কোষ পচে যায়৷', '২। পচা আলুতে এক ধরনের উগ্র গন্ধের সৃষ্টি হয়৷ চাপ দিলে আলু থেকে এক প্রকার দূষিত পানি বেরিয়ে আসে৷','৩। আক্রান্ত অংশ ঘিয়ে রংয়ের নরম হয় যাকে সহজেই সুস্থ অংশ থেকে আলাদা করা যায়৷'),CONCAT_WS(CHAR(10 using utf8),'প্রতিকার:', ' ', '১। সুস্থ ও রোগমুক্ত বীজ ব্যবহার করতে হবে৷', '২। অতিরিক্ত সেচ পরিহার করতে হবে৷','৩। উচ্চ তাপ এড়ানোর জন্য আগাম চাষ করতে হবে৷','৪। ভালোভাবে বাছাই করে আলু সংরক্ষণ করতে হবে৷','৫। ১% ব্লিচিং পাউডার অথবা ৩% বরিক এসিডের দ্রবণে টিউবার শোধন করে বীজ আলু সংরক্ষণ করতে হবে৷'));
